// Lista di keywords da analizzare
const keywords = [
  'wellness',
  'productivity',
  'fitness',
  'sexual wellness',
  'sustainability',
  'fotovoltaico',
  'benessere sessuale',
  'meditazione',
  'yoga',
  'integratori naturali'
];

return keywords.map(keyword => ({
  json: {
    keyword: keyword,
    geo: 'IT'
  }
}));

// Elabora i risultati da SerpAPI
// La keyword è nella risposta SerpAPI in search_parameters.q
const items = $input.all();
const processedTrends = [];

for (const item of items) {
  const serpData = item.json;

  // Recupera keyword dalla risposta SerpAPI
  const keyword = serpData.search_parameters?.q || 
                  serpData.query || 
                  'unknown';

  const geo = serpData.search_parameters?.geo || 'IT';

  // Estrai dati da SerpAPI response
  // La struttura è: interest_over_time.timeline_data[].values[].extracted_value
  const interestOverTime = serpData.interest_over_time?.timeline_data || 
                           serpData.timeline_data || 
                           [];

  if (interestOverTime.length > 0) {
    // Estrai i valori da timeline_data
    // Ogni elemento ha values[] array con extracted_value
    const scores = interestOverTime
      .map(d => {
        if (d.values && d.values.length > 0) {
          // Prendi il primo valore dall'array values
          return d.values[0].extracted_value || d.values[0].value || 0;
        }
        // Fallback per strutture alternative
        return d.value || d.interest || 0;
      })
      .filter(v => v > 0);

    const avgScore = scores.length > 0 ? scores.reduce((a, b) => a + b, 0) / scores.length : 0;
    const normalizedScore = (avgScore / 100) * 10;

    // Determina categoria
    let category = 'wellbeing';
    const keywordLower = keyword.toLowerCase();
    if (keywordLower.includes('sexual') || keywordLower.includes('sessual') || keywordLower.includes('benessere sessuale')) {
      category = 'sexual-wellbeing';
    } else if (keywordLower.includes('sustainability') || keywordLower.includes('sostenibilità') || keywordLower.includes('fotovoltaico') || keywordLower.includes('green') || keywordLower.includes('eco')) {
      category = 'sustainability';
    }

    processedTrends.push({
      keyword: keyword,
      source: 'google',
      score: parseFloat(normalizedScore.toFixed(2)),
      category: category,
      metadata: {
        region: geo,
        timeframe: 'last_12_months',
        rawScore: avgScore,
        dataPoints: interestOverTime.length,
        dateRange: serpData.search_parameters?.date || 'today 12-m'
      }
    });
  } else {
    // Fallback se non ci sono dati
    processedTrends.push({
      keyword: keyword,
      source: 'google',
      score: 5.0,
      category: 'wellbeing',
      metadata: {
        region: geo,
        timeframe: 'last_12_months',
        mock: true,
        note: 'No data from SerpAPI'
      }
    });
  }
}

return processedTrends.map(trend => ({ json: trend }));

// Raccogli tutti i trend in un array
const items = $input.all();
const trends = items.map(item => item.json);

return [{
  json: {
    trends: trends
  }
}];
